$('#cart-header .box1 .right .er').on('mouseenter', function () {
    $(this).children('#cart-header .box1 .right .er .erji').css({
        display: 'block'
    })
})

$('#cart-header .box1 .right .er').on('mouseleave', function () {
    $(this).children('#cart-header .box1 .right .er .erji').css({
        display: 'none'
    })
})




